import { SET_PERSONLIST, SET_FILTEREDPERSONLIST } from "../actions-types";

const initialState = {
    personList: [],
    filteredPersonList: []
}

export default function PersonListReducer(state = initialState, action) {
    switch(action.type) {
        case SET_PERSONLIST: 
            return {
                ...state,
                personList: [...action.payload]
            }
        case SET_FILTEREDPERSONLIST: 
            return {
                ...state,
                filteredPersonList: [...action.payload]
            }
        default:
            return state;
    }
}
