import { SET_PERSONLIST, ADD_PERSONLIST, DELETE_PERSONLIST, SET_FILTEREDPERSONLIST, UPDATE_FILTEREDPERSONLIST } from "./actions-types";

export const setPersonList = list => dispatch => {
    dispatch({type: SET_PERSONLIST, payload: list});
}

export const addPersonList = person => dispatch => {
    dispatch({type: ADD_PERSONLIST, payload: person}); 
}

export const deletePersonList = index => dispatch => {
    dispatch({type: DELETE_PERSONLIST, payload: index});
}

export const setFilteredPersonList = list => dispatch => {
    dispatch({type: SET_FILTEREDPERSONLIST, payload: list});
}

export const updateFilteredPersonList = (index, responseData) => dispatch => {
    dispatch({type: UPDATE_FILTEREDPERSONLIST, payload: [index, responseData]});
}
