import { SET_FORMDATA } from "../actions/actions-types";

// Objet de réinitialisation du formulaire d'ajout
const initialFormData = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: '1'
}

const initialState = {
    formData: {}
}

export default function FormDataReducer(state = initialState, action) {
    switch(action.type) {
        case SET_FORMDATA:
            return {
                ...state,
                personList: [...action.payload]
            }
        default:
            return state;
    }
}
