import React from 'react';
import { useState, useEffect } from 'react';
import $ from "jquery";
import './PersonForm.css';

const initialState = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: 'true'
}

export default function PersonForm( { setPersonList }) {
    const stateUpdate = target => {
        setFormData(prevState => {
            return {...prevState, [target.name]: target.value};
        });
    }

    async function postFormDataAsJson({ url, formData }) {
        const plainFormData = Object.fromEntries(formData.entries());
        const formDataJsonString = JSON.stringify(plainFormData);
        console.log(formDataJsonString);
    
        const fetchOptions = {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: formDataJsonString,
        };
    
        const response = await fetch(url, fetchOptions);
        console.log(response);
    
        if (!response.ok) {
            const errorMessage = await response.text();
            throw new Error(errorMessage);
        }
    
        return response.json();
    }
    
    /**
     * Event handler for a form submit event.
     *
     * @see https://developer.mozilla.org/en-US/docs/Web/API/HTMLFormElement/submit_event
     *
     * @param {SubmitEvent} event
     */
    async function handleFormSubmit(e) {
        e.preventDefault();
    
        const form = e.currentTarget;
        console.log(form);
        const url = form.action;
        console.log(url);
    
        try {
            const formData = new FormData(form);
            const responseData = await postFormDataAsJson({ url, formData });
            setPersonList(prevState => {
                return [...prevState, responseData];
            });
            console.log({ responseData });
        } catch (error) {
            console.error(error);
        }
    }

    const reinitializeFormData = () => {
        setFormData(initialState);
    }

    return (
        <form className="personForm" action="/person" onSubmit={handleFormSubmit}>
            <div className="personForm__item">
                <label>Nom</label>
                <input type="text" value={formData.firstname} name="firstname" onChange={e => stateUpdate(e.target)} />
            </div>
            <div className="personForm__item">
                <label>Pr√©nom</label>
                <input type="text" value={formData.lastname} name="lastname" onChange={e => stateUpdate(e.target)} />
            </div>
            <div className="personForm__item">
                <label>Adresse</label>
                <input type="text" value={formData.address} name="address" onChange={e => stateUpdate(e.target)} />
            </div>
            <div className="personForm__item">
                <label>Actif</label>
                <select onChange={e => stateUpdate(e.target)} value={formData.isActive} name="isActive">
                    <option value="true">Yes</option>
                    <option value="false">No</option>
                </select>
            </div>
            <div className="personForm__item">
                <input type="submit" value="Ajouter Personne" />
            </div>
        </form>
    )
}
