import { SET_PERSONLIST, SET_FILTEREDPERSONLIST, UPDATE_FILTEREDPERSONLIST, DELETE_FILTEREDPERSONLIST } from "../actions/actions-types";

const initialState = {
    personList: [],
    filteredPersonList: []
}

export default function PersonListReducer(state = initialState, action) {
    switch(action.type) {
        case SET_PERSONLIST: 
            return {
                ...state,
                personList: [...action.payload]
            }
        case SET_FILTEREDPERSONLIST: 
            return {
                ...state,
                filteredPersonList: [...action.payload]
            }
        case UPDATE_FILTEREDPERSONLIST:
            return {
                ...state,
                filteredPersonList: [...state.filteredPersonList.slice(0, action.payload[0]), action.payload[1], ...state.filteredPersonList.slice(action.payload[0] + 1)]
            }
        case DELETE_FILTEREDPERSONLIST:
            return {
                ...state,
                filteredPersonList: state.filteredPersonList.splice(action.payload, 1)
            }
        default:
            return state;
    }
}
