import React from 'react';
import { useEffect, useState } from 'react';
import './PersonList.css';
import $ from "jquery";
import Scrollbars from 'react-custom-scrollbars';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import SettingsIcon from '@material-ui/icons/Settings';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import SendIcon from '@material-ui/icons/Send';

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 0.5,
      maxWidth: 300,
    },
    listItem: {
      backgroundColor: 'none',
      "&:hover": {
          backgroundColor: 'rgba(0,0,0,0.05);',
          cursor: 'pointer'
      }
    },
    listItemText: {
        display: 'flex',
        justifyContent: 'left',
    },
    button: {
      margin: theme.spacing(1),
      [theme.breakpoints.down("sm")]: {
        minWidth: 32,
        paddingLeft: 8,
        paddingRight: 8,
        marginBottom: 40,
        cursor: "pointer",
        "& .MuiButton-startIcon": {
          margin: 0
        }
      }
    },
    buttonText: {
      [theme.breakpoints.down("sm")]: {
        display: "none"
      },
      display: "flex",
      marginRight: 5
    }
}));

export default function PersonList({ setPersonList, personList }) {
    const classes = useStyles();
    const [isEditing, setIsEditing] = useState(false);
    const [editedRowIndex, setEditedRowIndex] = useState(null);

    const filterCaseAccentInsensitive= (filter, row) => {
      const id = filter.pivotId || filter.id;
      const content = row[id];
      if (typeof content !== 'undefined') {
          // filter by text in the table or if it's a object, filter by key
          if (typeof content === 'object' && content !== null && content.key) {
              return String(content.key).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          } else {
              return String(content).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          }
      }
  
      return true;
    };

    const toggleIsEditing = (e) => {
        const personID = $($(e.currentTarget).parents()[3]).find('.personID').text();
        const editableIndex = personList.findIndex(item => item.id === parseInt(personID));

        setIsEditing((b) => !b);
        setEditedRowIndex(editableIndex);
    }   

    const EditableRow = ({value }) => (
        <React.Fragment>
        <div style={{ display: "flex", alignItems: "center" }}>
            <input
                className="MuiTypography-root MuiTypography-h4 MuiTypography-displayInline"
                value={JSON.parse(value)}
                // onChange={(e) => setValue(e.target.value)}
            />
            <Typography style={{ display: "none" }} />
        </div>
        </React.Fragment>
    );

    const deleteRow = (e) => {
        // Supprimer l'élément dans la base de données
        const personID = $($(e.currentTarget).parents()[3]).find('.personID').text();
        const myInit = {
            method: 'DELETE',
        };
        fetch(`person/${personID}`, myInit)
        .then(() => {
        // Récuperer l'index de l'élément à supprimer
        const removeIndex = personList.findIndex(item => item.id === parseInt(personID));

        // Faire une copie du state
        const newArray = [...personList];

        // Supprimer l'élément du nouveau tableau
        newArray.splice(removeIndex, 1);

        // Mettre à jour le state
        setPersonList(newArray);
        });
    };
  
    return (
        <>
        <Scrollbars style={{ width: '50%', height: '100%' }} renderTrackHorizontal={props => <div {...props} style={{display: 'none'}} className="track-horizontal"/>}>
          <Grid item xs={16} md={12}>
              <List>
                <ListItem className={classes.listItem} onClick={console.log('row selected')}>
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>ID</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>Prénom</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>Nom</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>Adresse</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>Actif</Typography>}
                            className={classes.listItemText}
                        />
                    </ListItem>
                {personList.map((item, index) => (
                    isEditing && index === editedRowIndex ? ( 
                        <ListItem className={classes.listItem} onClick={console.log('row selected')}>
                            <ListItemText
                                disableTypography
                                primary={<EditableRow value={item.id} />}
                                className={`${classes.listItemText} personID`}
                            />
                            <ListItemText
                                disableTypography
                                primary={<EditableRow value={item.firstname} />}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={<EditableRow value={item.lastname} />}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={<EditableRow value={item.address} />}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={<EditableRow value={item.isActive} />}
                                className={classes.listItemText}
                            />
                            <ListItemSecondaryAction>
                                <IconButton edge="end" aria-label="delete">
                                    <SendIcon 
                                        style={{fill: "#3f51b5"}} 
                                        onClick={console.log('content edited !')} 
                                    />
                                </IconButton>
                            </ListItemSecondaryAction>
                       </ListItem>
                        ) : (
                    <ListItem className={classes.listItem} onClick={console.log('row selected')}>
                        <ListItemText
                           disableTypography
                           primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.id}</Typography>}
                           className={`${classes.listItemText} personID`}
                        />
                        <ListItemText
                           disableTypography
                           primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.firstname}</Typography>}
                           className={classes.listItemText}
                        />
                         <ListItemText
                           disableTypography
                           primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.lastname}</Typography>}
                           className={classes.listItemText}
                        />
                        <ListItemText
                           disableTypography
                           primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.address}</Typography>}
                           className={classes.listItemText}
                        />
                        <ListItemText
                           disableTypography
                           primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.isActive}</Typography>}
                           className={classes.listItemText}
                        />
                       <ListItemSecondaryAction>
                       <IconButton edge="end" aria-label="delete">
                           <CloseIcon 
                             style={{fill: "#3f51b5"}} 
                             onClick={(e) => {
                                 deleteRow(e);
                             }} 
                           />
                       </IconButton>
                       <IconButton edge="end" aria-label="delete">
                           <SettingsIcon 
                             style={{fill: "#3f51b5"}} 
                             onClick={toggleIsEditing} 
                           />
                       </IconButton>
                       </ListItemSecondaryAction>
                   </ListItem>
                    )
                ))}
              </List>
          </Grid>
        </Scrollbars>
        </>
    )
}
