import React from 'react';
import { useState, useEffect } from 'react';
import './PersonList.css';
import $ from "jquery";
import Scrollbars from 'react-custom-scrollbars';
import { putFormDataAsJson, deletePerson, getAllPersonSorteredByColumn } from '../../api/PersonAPI';
import { convertFormDataToObject } from '../../utils/FormData';
import { getListOfFilteredID } from '../../utils/ListFilter';
import {
    List,
    ListItem,
    ListItemSecondaryAction,
    ListItemText,
    IconButton,
    Grid,
    Typography,
    Button,
    Chip
} from '@mui/material';
import {
    Close,
    FilterList,
    Send,
    Edit
} from '@mui/icons-material';


// Initialisation des valeurs de filtres
const initialFilteredPersonList = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: ''
}

const initialSortPersonList = {
    id: false,
    firstname: false,
    lastname: false,
    address: false,
    isActive: false
}

export default function PersonList({ setPersonList, personList }) {
    const [editedRowIndex, setEditedRowIndex] = useState(null);
    const [filterOptions, setFilterOptions]  = useState(initialFilteredPersonList);
    const [filteredListPerson, setFilteredListPerson] = useState([]);
    const [sortColumnsStatus, setSortColumnsStatus] = useState(initialSortPersonList);
    const [actualColumnSorted, setActualColumnSorted] = useState("id");

    // Gestion du state stockant les valeurs de filtrage
    const handleFormFilterInput = (e) => {
        const { value, name } = e.target;
        setFilterOptions(prevState => {
            return {...prevState, [name]: value}
        });
    }

    // permet de switcher sur le mode édition d'une ligne
    const toggleIsEditing = (e) => {
        const personID = $(e.currentTarget).data("idedit");
        const editableIndex = filteredListPerson.findIndex(item => parseInt(item.id) === parseInt(personID));
        setEditedRowIndex(editableIndex);
    }  
    
    // gestion du button delete
    const handleDelete = (e) => {
        e.preventDefault();
        e.stopPropagation();
        const personID = $(e.currentTarget).data("iddelete");
        deletePerson(personID).then(() => {
            // Récuperer l'index de l'élément à supprimer
            const removeIndex = filteredListPerson.findIndex(item => item.id === parseInt(personID));
        
            // Faire une copie du state
            const newArray = [...filteredListPerson];
        
            // Supprimer l'élément du nouveau tableau
            newArray.splice(removeIndex, 1);
        
            // Mettre à jour le state
            setFilteredListPerson(newArray);
        });
    }

    // Element React pour la modification d'une ligne
    const EditableRow = ({ value, name, type, lastRow }) => {
        return (
            <React.Fragment>
            <div style={{ display: "flex", justifyContent: "flex-start", width: lastRow ? '70%' : '100%' }}>
                <input
                    defaultValue={value}
                    name={name}
                    type={type}
                    style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                />
            </div>
            </React.Fragment>
        )
    };

    // Gérer le formulaire de la mise à jour d'une personne
    const handleFormSubmit = async (e) => {
        e.preventDefault();

        // Récupérer les données du formulaire
        const personID = $(e.currentTarget).find('.personID').find('input').attr('value');
        const updateIndex = filteredListPerson.findIndex(item => parseInt(item.id) === parseInt(personID));
        const url = `person/${personID}`;

        try {
            // Requête PUT
            const { formData, _} = convertFormDataToObject(e);
            let responseData = await putFormDataAsJson({ formData, url });

            // Mise à jour de la liste des utilisateurs
            setPersonList(prevState => {
                return  [...prevState.slice(0, updateIndex), responseData, ...prevState.slice(updateIndex + 1)];
            });

            setEditedRowIndex(-1);
        } catch (error) {
            console.error(error);
        }
    }

    const sortColumn = (col) => {
        setSortColumnsStatus(prevState => {
            return {...prevState, [col]: !sortColumnsStatus[col]}
        });
        console.log(col);
        setActualColumnSorted(col);
        $('.columnFilter').val("");
    }

    // Initialisation du state stockant la liste filtrée
    useEffect(() => {
        setFilteredListPerson(personList);
    }, [personList]);

    // Appel à l'algorithme à chaque fois qu'une valeur de filtrage change
    useEffect(() => {
        const filteredID = getListOfFilteredID(filterOptions, personList);
        if (filteredID.length != 0) {
            setFilteredListPerson(personList.filter((item, index) => filteredID.includes(String(item.id))));
        }
    }, [filterOptions]);

    useEffect(() => {
        if (sortColumnsStatus[actualColumnSorted]) {
            getAllPersonSorteredByColumn(actualColumnSorted, "desc").then(data => {
                setFilteredListPerson(data);
            }); 
        } else {
            getAllPersonSorteredByColumn(actualColumnSorted, "asc").then(data => {
                setFilteredListPerson(data);
            }); 
        }
    }, [actualColumnSorted, sortColumnsStatus]);


    return (
        <>
        <Scrollbars style={{ width: '70%', height: '100%' }} renderTrackHorizontal={props => <div {...props} style={{display: 'none'}} className="track-horizontal"/>}>
          <Grid item xs={16} md={12}>
              <List className='list'>
                    <ListItem className='list-item' style={{ backgroundColor: '#9AC2C980' }}>
                        <ListItemText
                            disableTypography
                            primary={<Button type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }} onClick={() => sortColumn("id")} endIcon={<FilterList />}>ID</Button>}
                            className='list-item-text'
                        />
                        <ListItemText
                            disableTypography
                            primary={<Button type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }} onClick={() => sortColumn("firstname")} endIcon={<FilterList />}>Prénom</Button>}
                            className='list-item-text'
                        />
                        <ListItemText
                            disableTypography
                            primary={<Button type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }} onClick={() => sortColumn("lastname")} endIcon={<FilterList />}>Nom</Button>}
                            className='list-item-text'
                        />
                        <ListItemText
                            disableTypography
                            primary={<Button type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }} onClick={() => sortColumn("address")} endIcon={<FilterList />}>Adresse</Button>}
                            className='list-item-text'
                        />
                        <ListItemText
                            disableTypography
                            primary={<Button type="body2" style={{ fontWeight: 700, width: 10, color: '#004040', paddingRight: 30 }} onClick={() => sortColumn("isActive")} endIcon={<FilterList />}>Actif</Button>}
                            className='list-item-text'
                        />
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete" type="submit">
                                <Send 
                                    style={{display: "none"}} 
                                />
                            </IconButton>
                        </ListItemSecondaryAction>
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete" type="submit">
                                <Send 
                                    style={{display: "none"}} 
                                />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                    <form className="filterForm">
                        <ListItem className='list-item'>
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "flex-start", width: '100%' }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline columnFilter"
                                        defaultValue={filterOptions["id"]}
                                        onChange={handleFormFilterInput}
                                        name="id"
                                        style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                                        placeholder="Filtrer"
                                    />
                                </div>}
                                className='list-item-text'
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "flex-start", width: '100%' }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline columnFilter"
                                        defaultValue={filterOptions["firstname"]}
                                        onChange={handleFormFilterInput}
                                        name="firstname"
                                        style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                                        placeholder="Filtrer"
                                    />
                                </div>}
                                className='list-item-text'
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "flex-start", width: '100%' }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline columnFilter"
                                        defaultValue={filterOptions["lastname"]}
                                        onChange={handleFormFilterInput}
                                        name="lastname"
                                        style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                                        placeholder="Filtrer"
                                    />
                                </div>}
                                className='list-item-text'
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "flex-start", width: '100%' }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline columnFilter"
                                        defaultValue={filterOptions["address"]}
                                        onChange={handleFormFilterInput}
                                        name="address"
                                        style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                                        placeholder="Filtrer"    
                                    />
                                </div>}
                                className='list-item-text'
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "flex-start", width: '70%' }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline columnFilter"
                                        defaultValue={filterOptions["isActive"]}
                                        onChange={handleFormFilterInput}
                                        name="isActive"
                                        style={{border: '#008080 solid 0.5px', width: '100%', margin: '2px'}}
                                        placeholder="Filtrer"
                                    />
                                </div>}
                                className='list-item-text'
                            />
                        </ListItem>
                    </form>
                {filteredListPerson.map((item, index) => (
                    index === editedRowIndex ? ( 
                        <form className="editForm" onSubmit={handleFormSubmit}>
                            <ListItem className='list-item'>
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.id} name="id" />}
                                    className='list-item-text personID'
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.firstname} name="firstname" />}
                                    className='list-item-text'
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.lastname} name="lastname" />}
                                    className='list-item-text'
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.address} name="address" type={"email"}/>}
                                    className='list-item-text'
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.isActive} name="isActive" lastRow={true} />}
                                    className='list-item-text'
                                />
                                <ListItemSecondaryAction>
                                    <IconButton edge="end" aria-label="edit" type="submit">
                                        <Send 
                                            style={{fill: "#008080"}} 
                                        />
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                       </form>
                        ) : (
                    <ListItem className='list-item'>
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2">{item.id}</Typography>}
                            className='list-item-text personID'
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" >{item.firstname}</Typography>}
                            className='list-item-text'
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2">{item.lastname}</Typography>}
                            className='list-item-text'
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2">{item.address}</Typography>}
                            className='list-item-text'
                            />
                            <ListItemText
                            disableTypography
                            primary={parseInt(item.isActive) ? <Chip style={{backgroundColor: 'rgba(53, 219, 94, 0.8)', color: 'white', fontWeight: 700}} label="Actif" /> : <Chip style={{backgroundColor: 'rgba(209, 42, 23, 0.8)', color: 'white', fontWeight: 700}} label="Inactif" />}
                            className='list-item-text'
                            />
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="edit" onClick={toggleIsEditing}>
                                <Edit 
                                    style={{fill: "#008080"}} 
                                    className="editable-icon"
                                />
                            </IconButton>
                            <IconButton edge="end" aria-label="delete" onClick={handleDelete} data-iddelete={item.id}>
                                <Close 
                                    style={{fill: "#008080"}} 
                                />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                    )
                ))}
              </List>
          </Grid>
        </Scrollbars>
        </>
    )
}
