import React from 'react';
import { useState, useEffect } from 'react';
import './PersonList.css';
import $ from "jquery";
import Scrollbars from 'react-custom-scrollbars';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemSecondaryAction from '@material-ui/core/ListItemSecondaryAction';
import ListItemText from '@material-ui/core/ListItemText';
import IconButton from '@material-ui/core/IconButton';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import SendIcon from '@material-ui/icons/Send';
import EditIcon from '@material-ui/icons/Edit';


// Initialisation des valeurs de filtres
const initialFilteredPerson = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: ''
}

export default function PersonList({ setPersonList, personList }) {
    const classes = useStyles();
    const [editedRowIndex, setEditedRowIndex] = useState(null);
    const [filterOptions, setFilterOptions]  = useState(initialFilteredPerson);
    const [filteredListPerson, setFilteredListPerson] = useState([]);

    // Gestion du state stockant les valeurs de filtrage
    const handleFormFilterInput = (e) => {
        const { value, name } = e.target;
        setFilterOptions(prevState => {
            return {...prevState, [name]: value}
        });
    }

    // Algorithme de filtrage de la liste
    const filter = () => {
        let counter = {}
        personList.map(item => counter[item.id] = 0);
        personList.map(item => {
            Object.entries(item).map(val => {
                if (String(val[1]).toLowerCase().startsWith(String(filterOptions[val[0]]).toLowerCase()) && String(filterOptions[val[0]]) != '') {
                    counter[item["id"]] += 1;
                }
            });  
        });
        const numberOfFieldsFilled = Object.values(filterOptions).filter(Boolean).length;
        counter = Object.entries(counter).filter(val => val[1] === numberOfFieldsFilled);
        counter = Object.fromEntries(counter);
        const idFiltered = Object.keys(counter);
        if (idFiltered.length != 0) {
            setFilteredListPerson(personList.filter(item => idFiltered.includes(String(item.id))));
        }
    };

    // Initialisation du state stockant la liste filtrée
    useEffect(() => {
        setFilteredListPerson(personList)
    }, [personList]);

    // Appel à l'algorithme à chaque fois qu'une valeur de filtrage change
    useEffect(() => {
        filter();
    }, [filterOptions]);

    // permet de switcher sur le mode édition d'une ligne
    const toggleIsEditing = (e) => {
        if ($(e.currentTarget).attr('class').includes('editableIcon')) {
            const personID = $($(e.currentTarget).parents()[3]).find('.personID').text();
            const editableIndex = personList.findIndex(item => parseInt(item.id) === parseInt(personID));
            setEditedRowIndex(editableIndex);
        }
    }   

    // Element React pour la modification d'une ligne
    const EditableRow = ({ value, name }) => {
        return (
            <React.Fragment>
            <div style={{ display: "flex", alignItems: "center" }}>
                <input
                    className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                    defaultValue={value}
                    name={name}
                />
                <Typography style={{ display: "none" }} />
            </div>
            </React.Fragment>
        )
    };

    // Supprimer une personne de la liste
    const deleteRow = (e) => {
        // Supprimer l'élément dans la base de données
        const personID = $($(e.currentTarget).parents()[3]).find('.personID').text();
        const myInit = {
            method: 'DELETE',
        };
        fetch(`person/${personID}`, myInit)
        .then(() => {
        // Récuperer l'index de l'élément à supprimer
        const removeIndex = personList.findIndex(item => item.id === parseInt(personID));

        // Faire une copie du state
        const newArray = [...personList];

        // Supprimer l'élément du nouveau tableau
        newArray.splice(removeIndex, 1);

        // Mettre à jour le state
        setPersonList(newArray);
        });
    };

    // Convertir les données du formulaire sous la forme d'un objet js
    const convertFormDataToObject = (e) => {
        const form = e.currentTarget;
        return new FormData(form);
    }

    // Gérer le formulaire de la mise à jour d'une personne
    const handleFormSubmit = async (e) => {
        e.preventDefault();

        // Récupérer les données du formulaire
        const personID = $(e.currentTarget).find('.personID').find('input').attr('value');
        const updateIndex = personList.findIndex(item => parseInt(item.id) === parseInt(personID));
        const url = `person/${personID}`;

        try {
            // Requête PUT
            const formData = convertFormDataToObject(e);
            let responseData = await putFormDataAsJson({ url, formData });

            // Mise à jour de la liste des utilisateurs
            setPersonList(prevState => {
                return  [...prevState.slice(0, updateIndex), responseData, ...prevState.slice(updateIndex + 1)];
            });

            setEditedRowIndex(-1);
        } catch (error) {
            console.error(error);
        }
    }

    // REQUETE PUT via l'api person
    const putFormDataAsJson = async ({ url, formData }) => {
        const plainFormData = Object.fromEntries(formData.entries());
        const formDataJsonString = JSON.stringify(plainFormData);

        const fetchOptions = {
            method: "PUT",
            headers: {
                "Content-Type": "application/json",
                "Accept": "application/json",
            },
            body: formDataJsonString,
        };
    
        const response = await fetch(url, fetchOptions);

        if (!response.ok) {
            const errorMessage = await response.text();
            throw new Error(errorMessage);
        }
    
        return response.json();
    }
  
    return (
        <>
        <Scrollbars style={{ width: '70%', height: '100%' }} renderTrackHorizontal={props => <div {...props} style={{display: 'none'}} className="track-horizontal"/>}>
          <Grid item xs={16} md={12}>
              <List className={classes.List}>
                    <ListItem className={classes.listItem} style={{ backgroundColor: '#9AC2C980' }}>
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }}>ID</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }}>Prénom</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }}>Nom</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10, color: '#004040' }}>Adresse</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10, color: '#004040', paddingRight: 30 }}>Actif</Typography>}
                            className={classes.listItemText}
                        />
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete" type="submit">
                                <SendIcon 
                                    style={{display: "none"}} 
                                />
                            </IconButton>
                        </ListItemSecondaryAction>
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete" type="submit">
                                <SendIcon 
                                    style={{display: "none"}} 
                                />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                    <form className="filterForm">
                        <ListItem className={classes.listItem}>
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "center", width: 300 }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                                        defaultValue={filterOptions["id"]}
                                        onChange={handleFormFilterInput}
                                        name={"id"}
                                        style={{border: '#004040 solid 2px', width: 100}}
                                    />
                                </div>}
                                className={classes.listItemText} 
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "center", width: 300 }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                                        defaultValue={filterOptions["firstname"]}
                                        onChange={handleFormFilterInput}
                                        name={"firstname"}
                                        style={{border: '#004040 solid 2px', width: 150}}
                                    />
                                </div>}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "center", width: 300 }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                                        defaultValue={filterOptions["lastname"]}
                                        onChange={handleFormFilterInput}
                                        name={"lastname"}
                                        style={{border: '#004040 solid 2px', width: 100}}
                                    />
                                </div>}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "center", width: 300 }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                                        defaultValue={filterOptions["address"]}
                                        onChange={handleFormFilterInput}
                                        name={"address"}
                                        style={{border: '#004040 solid 2px', width: 150}}
                                    />
                                </div>}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={
                                <div style={{ display: "flex", justifyContent: "center", width: 300 }}>
                                    <input
                                        className="MuiTypography-root MuiTypography-h7 MuiTypography-displayInline"
                                        defaultValue={filterOptions["isActive"]}
                                        onChange={handleFormFilterInput}
                                        name={"isActive"}
                                        style={{border: '#004040 solid 2px', width: 150}}
                                    />
                                </div>}
                                className={classes.listItemText}
                            />
                            <ListItemText
                                disableTypography
                                primary={<Typography type="body2" style={{ width: 50 }}></Typography>}
                                className={classes.listItemText}
                            />
                        </ListItem>
                    </form>
                {filteredListPerson.map((item, index) => (
                    index === editedRowIndex ? ( 
                        <form className="editForm" onSubmit={handleFormSubmit}>
                            <ListItem className={classes.listItem}>
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.id} name={"id"} />}
                                    className={`${classes.listItemText} personID`}
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.firstname} name={"firstname"} />}
                                    className={classes.listItemText}
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.lastname} name={"lastname"} />}
                                    className={classes.listItemText}
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.address} name={"address"} />}
                                    className={classes.listItemText}
                                />
                                <ListItemText
                                    disableTypography
                                    primary={<EditableRow value={item.isActive} name={"isActive"} />}
                                    className={classes.listItemText}
                                />
                                <ListItemSecondaryAction>
                                    <IconButton edge="end" aria-label="delete" type="submit">
                                        <SendIcon 
                                            style={{fill: "#008080"}} 
                                        />
                                    </IconButton>
                                </ListItemSecondaryAction>
                            </ListItem>
                       </form>
                        ) : (
                        <ListItem className={classes.listItem}>
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.id}</Typography>}
                            className={`${classes.listItemText} personID`}
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.firstname}</Typography>}
                            className={classes.listItemText}
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.lastname}</Typography>}
                            className={classes.listItemText}
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.address}</Typography>}
                            className={classes.listItemText}
                            />
                            <ListItemText
                            disableTypography
                            primary={<Typography type="body2" style={{ fontWeight: 700, width: 10 }}>{item.isActive}</Typography>}
                            className={classes.listItemText}
                            />
                        <ListItemSecondaryAction>
                        <IconButton edge="end" aria-label="delete">
                            <CloseIcon 
                                style={{fill: "#008080"}} 
                                onClick={(e) => {
                                    deleteRow(e);
                                }} 
                            />
                        </IconButton>
                        <IconButton edge="end" aria-label="edit">
                            <EditIcon 
                                style={{fill: "#008080"}} 
                                onClick={toggleIsEditing}
                                className="editableIcon" 
                            />
                        </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                    )
                ))}
              </List>
          </Grid>
        </Scrollbars>
        </>
    )
}

const useStyles = makeStyles((theme) => ({
    listItem: {
      backgroundColor: 'none',
      "&:hover": {
          backgroundColor: 'rgba(0,0,0,0.05);',
          cursor: 'pointer'
      },
      paddingTop: 0,
      paddingBottom: 0,
    },
    listItemText: {
        display: 'flex',
        justifyContent: 'center',
        paddingRight: 5
    },
    button: {
      margin: theme.spacing(1),
      [theme.breakpoints.down("sm")]: {
        minWidth: 32,
        paddingLeft: 8,
        paddingRight: 8,
        marginBottom: 40,
        cursor: "pointer",
        "& .MuiButton-startIcon": {
          margin: 0
        }
      }
    },
    buttonText: {
      [theme.breakpoints.down("sm")]: {
        display: "none"
      },
      display: "flex",
      marginRight: 5
    },
    List: {
        padding: 0,
    }
}));
