import React from 'react';
import { useState, useEffect } from 'react';

export default function PersonForm() {
    const [formData, setFormData] = useState(null);

    let initialState = {
        id: '',
        firstname: '',
        lastname: '',
        address: '',
        isActive: ''
    }

    useEffect(() => {
        setFormData(initialState);
    }, [formData]);


    return (
        <div className="personForm">
            <div className="personForm__firstname">
                <label>FirstName</label>
                <input type="text" value={formData.firstname}></input>
            </div>
            <div className="personForm__lastname">
                <label>LastName</label>
                <input type="text" value={formData.lastname}></input>
            </div>
            <div className="personForm__address">
                <label>Address</label>
                <input type="text" value={formData.address}></input>
            </div>
            <div className="personForm__isActive">
                <label>isActive</label>
                <input type="text" value={formData.isActive}></input>
            </div>
        </div>
    )
}
