import React from 'react';
import { useEffect } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import './PersonList.css';
import $ from "jquery";
import Scrollbars from 'react-custom-scrollbars';

//  <div style="position: absolute; width: 6px; right: 2px; bottom: 2px; top: 2px; border-radius: 3px; background-color: rgb(238, 238, 238);"><div style="position: relative; display: block; width: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2); height: 375px; transform: translateY(0px);"></div>
// <div style="position: relative; display: block; width: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2); height: 375px; transform: translateY(0px);"></div>
// </div> 

export default function PersonList({ data }) {
    const filterCaseAccentInsensitive= (filter, row) => {
      const id = filter.pivotId || filter.id;
      const content = row[id];
      if (typeof content !== 'undefined') {
          // filter by text in the table or if it's a object, filter by key
          if (typeof content === 'object' && content !== null && content.key) {
              return String(content.key).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          } else {
              return String(content).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          }
      }
  
      return true;
    };

    const renderTrackVertical = ({ style, ...props }) => {
        const finalStyle = {
            ...style,
            right: 2,
            bottom: 2,
            top: 2,
            borderRadius: 3,
            backgroundColor: '#eee',
        };
        return <div style={finalStyle} {...props} />;
    }
  
    
    useEffect(() => {
      $('.rt-th > input').attr('autocomplete', 'nope');
      $('.rt-tbody').wrap($('<div></div>', {
          class: 'wrapper1',
          css: { 'position': 'absolute', 'inset': '0px', 'overflow': 'scroll', 'margin-right': '-15px', 'margin-bottom': '-15px' }
      }));
      $('.wrapper1').wrap($('<div></div>', {
        class: 'track-vertical',
        css: { 'position': 'relative', 'overflow': 'hidden', 'width': '50%', 'height': '100%' }
      }));
      $('<div class="wrapper2" style="position: absolute; height: 6px; right: 2px; bottom: 2px; left: 2px; border-radius: 3px;"><div style="position: relative; display: block; height: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2);"><div style="position: relative; display: block; height: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2);"></div></div>').insertAfter('.wrapper1');
      $('<div style="position: absolute; width: 6px; right: 2px; bottom: 2px; top: 2px; border-radius: 3px; background-color: rgb(238, 238, 238);"><div style="position: relative; display: block; width: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2);"><div style="position: relative; display: block; width: 100%; cursor: pointer; border-radius: inherit; background-color: rgba(0, 0, 0, 0.2);"></div></div>').insertAfter('.wrapper2')
      
    //   $('.rt-tbody').wrap($('<div></div>', {
    //     class: 'wrapper1',
    //     css: { 'position': 'relative', 'display': 'block', 'width': '100%', 'cursor': 'pointer', 'border-radius': 'inherit', 'background-color': 'rgba(0, 0, 0, 0.2)', 'height': '375px', 'transform': 'translateY(0px)' }
    //   }));
    //   $('.wrapper1').wrap($('<div></div>', {
    //     class: 'wrapper2',
    //     css: { 'position': 'absolute', 'width': '6px', 'right': '2px', 'bottom': '2px', 'top': '2px', 'border-radius': '3px', 'background-color': 'rgb(238, 238, 238)' }
    //   }));
    }, []);
  
    return (
        <Scrollbars style={{ width: '50%', height: '100%' }} renderTrackVertical={renderTrackVertical} className="track-vertical">
            <div id="drag-container">
                <div id="draggable">
                    <ReactTable
                    data={data}
                    columns={[
                        {
                        Header: 'Prénom',
                        accessor: 'firstname',
                        Cell: ({ row }) =><span className="text-wrap">{row.firstname}</span>
                        },
                        {
                        Header: 'Nom',
                        accessor: 'lastName',
                        Cell: ({ row }) => <span className="text-wrap">{row.lastname}</span>
                        },
                        {
                        Header: 'Adresse',
                        accessor: 'address',
                        Cell: ({ row }) => <span className="text-wrap">{row.address}</span>
                        },
                        {
                        Header: 'Actif',
                        accessor: 'isActive',
                        Cell: ({ row }) => <span className="text-wrap">{row.isActive}</span>
                        }
                    ]}
                    defaultPageSize={15}
                    className="-striped -highlight"
                    resizable={true}
                    //   getTrProps={(state, rowInfo, instance) => {
                    //     return {
                    //       onClick: (e, handleOriginal) => {
                    //         $(e.currentTarget).parents('.rt-tr-group').nextAll().each((index, item) => $(item).css('background-color', 'transparent'));
                    //         $(e.currentTarget).parents('.rt-tr-group').nextAll().find('span').css('color', 'black');
                    //         $(e.currentTarget).parents('.rt-tr-group').nextAll().each((index, item) => $(item).removeClass('clicked'));
                    
                    //         $(e.currentTarget).parents('.rt-tr-group').prevAll().each((index, item) =>  $(item).css('background-color', 'transparent'));
                    //         $(e.currentTarget).parents('.rt-tr-group').prevAll().find('span').css('color', 'black');
                    //         $(e.currentTarget).parents('.rt-tr-group').prevAll().each((index, item) => $(item).removeClass('clicked'));
                    
                    //         if($(e.currentTarget).parents('.rt-tr-group').hasClass('clicked')) {
                    //           $(e.currentTarget).parents('.rt-tr-group').css('background-color', 'transparent');
                    //           $(e.currentTarget).parents('.rt-tr-group').each((index, item) => $(item).find('span').css('color', 'black'));
                    //         } else {
                    //           $(e.currentTarget).parents('.rt-tr-group').css('background-color', 'rgba(60, 84, 180, 0.35)');
                    //           $(e.currentTarget).parents('.rt-tr-group').each((index, item) => $(item).find('span').css('color', 'white'));
                    //         }
        
                    //         setIsUserUpdate(rowInfo.original.Update);
                    //         setCurrentUser(rowInfo.original);
                    //         setFirstName(rowInfo.original.FirstName);
                    //         setLastName(rowInfo.original.LastName);
                    //         setEmail(rowInfo.original.Email);
                    //         setKnownGroups(rowInfo.original.AffectedGroups.split('; |'));
                    //         reset();
                    //         setDisableStep1(true);
                    //         setDisableStep2(false);
                    //       }
                    //     };
                    //   }}
                    filterable
                    defaultFilterMethod={filterCaseAccentInsensitive}
                    />
                </div>
            </div>
        </Scrollbars>
    )
}
