import './App.css';
import PersonForm from './components/PersonForm/PersonForm';
import PersonList from './components/PersonList/PersonList';
import { useState, useEffect } from 'react';

const initialState = {
  id: '',
  firstname: '',
  lastname: '',
  address: '',
  isActive: 'true'
}

function App() {
  const [personList, setPersonList] = useState([]);
  const [formData, setFormData] = useState(initialState);

  const getAllPerson = () => {
    const myInit = {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    };
    fetch('/person', myInit)
      .then(response => {
        if(response.ok) {
          return response.json();
        }
        throw response;
      })
      .then(data => setPersonList(data));
  }

  useEffect(() => {
    getAllPerson();
  }, []);

  console.log(personList);

  // useEffect(() => {
  //   getAllPerson();
  // }, [personList]);


  return (
    <div className="App">
      <PersonForm setPersonList={setPersonList} setFormData={setFormData} formData={formData} style={{ backgroundColor: '#1169cf' }} initialState={initialState} />
      <PersonList setFormData={setFormData} setPersonList={setPersonList} personList={personList} />
    </div>
  );
}

export default App;
