import React from 'react';
import { useEffect } from 'react';
import ReactTable from 'react-table';
import 'react-table/react-table.css';
import './PersonList.css';
import $ from "jquery";
import Scrollbars from 'react-custom-scrollbars';

export default function PersonList({ data, setFormData }) {
    const filterCaseAccentInsensitive= (filter, row) => {
      const id = filter.pivotId || filter.id;
      const content = row[id];
      if (typeof content !== 'undefined') {
          // filter by text in the table or if it's a object, filter by key
          if (typeof content === 'object' && content !== null && content.key) {
              return String(content.key).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          } else {
              return String(content).toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, "").includes(filter.value.toLowerCase().normalize('NFD').replace(/[\u0300-\u036f]/g, ""));
          }
      }
  
      return true;
    };

    const renderTrackVertical = ({ style, ...props }) => {
        const finalStyle = {
            ...style,
            right: 2,
            bottom: 2,
            top: 2,
            borderRadius: 3,
            backgroundColor: '#eee',
        };
        return <div style={finalStyle} {...props} />;
    }

    const updateRow = (e) => {
        console.log($(e.currentTarget).parents());
    }

    const deleteRow = (e) => {
        console.log($(e.currentTarget).parents());
    }
  
    useEffect(() => {
      $('.rt-th > input').attr('autocomplete', 'nope');
      $('.rt-thead').wrapAll("<div class='t-headers'></div>");
    }, []);
  
    return (
        <Scrollbars style={{ width: '50%', height: '100%' }} renderTrackVertical={renderTrackVertical} className="track-vertical">
            <div id="drag-container">
                <div id="draggable">
                    <ReactTable
                    data={data}
                    columns={[
                        {
                            Header: 'PrÃ©nom',
                            accessor: 'firstname',
                            Cell: ({ row }) =><span className="text-wrap">{row.firstname}</span>
                        },
                        {
                            Header: 'Nom',
                            accessor: 'lastname',
                            Cell: ({ row }) => <span className="text-wrap">{row.lastname}</span>
                        },
                        {
                            Header: 'Adresse',
                            accessor: 'address',
                            Cell: ({ row }) => <span className="text-wrap">{row.address}</span>
                        },
                        {
                            Header: 'Actif',
                            accessor: 'isActive',
                            Cell: ({ row }) => <span className="text-wrap">{row.isActive ? 'Actif' : 'Non actif'}</span>
                        }
                    ]}
                    defaultPageSize={10}
                    className="-striped -highlight"
                    resizable={true}
                    showPagination={true}
                    getTrProps={(state, rowInfo, instance) => {
                    return {
                        onClick: (e, handleOriginal) => {
                            const updateState = {
                                id: '',
                                firstname: '',
                                lastname: '',
                                address: '',
                                isActive: ''
                            }
                            updateState.map()
                            $(e.currentTarget).children().each((index, value) => console.log($(value).children().text()));
                        }
                    };
                    }}
                    filterable
                    defaultFilterMethod={filterCaseAccentInsensitive}
                    />
                </div>
            </div>
        </Scrollbars>
    )
}
