import React from 'react';
import { useState, useEffect } from 'react';
import './PersonForm.css';

const initialState = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: true
}

export default function PersonForm() {
    const [formData, setFormData] = useState(initialState);

    const stateUpdate = target => {
        let result = target.name === 'isActive' ? Boolean(parseInt(target.value)) : target.value;
        setFormData(prevState => {
            return {...prevState, [target.name]: result};
        });
    }

    useEffect(() => {
        console.log(formData);
    }, [formData])

    return (
        <div className="personForm">
            <div className="personForm__item">
                <label>Nom</label>
                <input type="text" value={formData.firstname} name="firstname" onChange={e => stateUpdate(e.target)}></input>
            </div>
            <div className="personForm__item">
                <label>Pr√©nom</label>
                <input type="text" value={formData.lastname} name="lastname" onChange={e => stateUpdate(e.target)}></input>
            </div>
            <div className="personForm__item">
                <label>Adresse</label>
                <input type="text" value={formData.address} name="address" onChange={e => stateUpdate(e.target)}></input>
            </div>
            <div className="personForm__item">
                <label>Actif</label>
                <select onChange={e => stateUpdate(e.target)} value={formData.isActive} name="isActive">
                    <option value={1}>Yes</option>
                    <option value={0}>No</option>
                </select>
            </div>
        </div>
    )
}
