import React from 'react';
import { useState, useEffect } from 'react';
import './PersonForm.css';

const initialState = {
    id: '',
    firstname: '',
    lastname: '',
    address: '',
    isActive: true
}

export default function PersonForm() {
    const [formData, setFormData] = useState(initialState);

    const stateUpdate = target => {
        setFormData(prevState => {
            return {prevState, [target.name]: target.value};
        });
    } 

    return (
        <div className="personForm">
            <div className="personForm__item">
                <label>Nom</label>
                <input type="text" value={formData.firstname} name="firstname"></input>
            </div>
            <div className="personForm__item">
                <label>Pr√©nom</label>
                <input type="text" value={formData.lastname} name="lastname"></input>
            </div>
            <div className="personForm__item">
                <label>Adresse</label>
                <input type="text" value={formData.address} name="address"></input>
            </div>
            <div className="personForm__item">
                <label>Actif</label>
                <select onChange={e => stateUpdate(e.target)} value={formData.isActive} name="isActive">
                    <option value={1}>Yes</option>
                    <option value={0}>No</option>
                </select>
            </div>
        </div>
    )
}
